mapper.insert:
INSERT INTO POST
	( ID
	  , TITLE
	  , CONTENT
	  , NAME
	  , CREATED_AT
	  , UPDATED_AT )
VALUES ( #{id}
	  , #{title}
	  , #{content}
	  , #{name}
	  , NOW()
	  , NOW())

mapper.update:
UPDATE POST
SET ID=#{id}
	, TITLE=#{title}
	, CONTENT=#{content}
	, NAME=#{name}
	, UPDATED_AT=NOW()
	  WHERE ID=#{id}
